bool isPrime(int num) {
	if (num < 2) return false;
	for (int i = 2; i * i <= num; i++) {
		if (num % i == 0) return false;
	}
	return true;
}

int main() {
	for (int i = 1; i <= 20; i++) {
		if (isPrime(i)) cout << i << " is a prime number" << endl;
	}
	return 0;
}

