int findLargest(int arr[], int size) {
	int largest = arr[0];
	for (int i = 1; i < size; i++) {
		if (arr[i] > largest) {
			largest = arr[i];
		}
	}
	return largest;
}

int main() {
	int numbers[] = {3, 5, 7, 2, 8};
	int size = sizeof(numbers) / sizeof(numbers[0]);
	cout << "Largest number: " << findLargest(numbers, size) << endl;
	return 0;
}

