#include <iostream>
using namespace std;

void bubbleSort(int arr[], int size) {
	for (int i = 0; i < size - 1; i++) {
		for (int j = 0; j < size - i - 1; j++) {
			if (arr[j] > arr[j + 1]) {
				swap(arr[j], arr[j + 1]);
			}
		}
	}
}

int main() {
	int arr[] = {64, 34, 25, 12, 22, 11, 90};
	int size = sizeof(arr) / sizeof(arr[0]);
	bubbleSort(arr, size);
	cout << "Sorted array: ";
	for (int i = 0; i < size; i++) cout << arr[i] << " ";
	return 0;
}

#include <iostream>
using namespace std;

class Queue {
	private:
		int arr[100];
		int front, rear;
		int capacity;
	public:
		Queue(int c) {
			capacity = c;
			front = rear = 0;
		}

		void enqueue(int item) {
			if (rear == capacity) {
				cout << "Queue is full" << endl;
				return;
			}
			arr[rear++] = item;
		}

		void dequeue() {
			if (front == rear) {
				cout << "Queue is empty" << endl;
				return;
			}
			front++;
		}

		void display() {
			for (int i = front; i < rear; i++) {
				cout << arr[i] << " ";
			}
			cout << endl;
		}
};

int main() {
	Queue q(5);
	q.enqueue(10);
	q.enqueue(20);
	q.display();
	q.dequeue();
	q.display();
	return 0;
}

