class Node {
	public:
		int data;
		Node* next;
		Node(int val) : data(val), next(nullptr) {}
};

class LinkedList {
	private:
		Node* head;
	public:
		LinkedList() : head(nullptr) {}
		void insert(int val) {
			Node* newNode = new Node(val);
			if (!head) {
				head = newNode;
			} else {
				Node* temp = head;
				while (temp->next) {
					temp = temp->next;
				}
				temp->next = newNode;
			}
		}
		void display() {
			Node* temp = head;
			while (temp) {
				cout << temp->data << " ";
				temp = temp->next;
			}
			cout << endl;
		}
};

int main() {
	LinkedList list;
	list.insert(1);
	list.insert(2);
	list.insert(3);
	list.display();
	return 0;
}

#include <iostream>
using namespace std;

class Stack {
	private:
		int arr[100];
		int top;
	public:
		Stack() {
			top = -1;
		}

		void push(int x) {
			if (top >= 99) {
				cout << "Stack Overflow" << endl;
				return;
			}
			arr[++top] = x;
		}

		void pop() {
			if (top < 0) {
				cout << "Stack Underflow" << endl;
				return;
			}
			cout << "Popped: " << arr[top--] << endl;
		}

		void display() {
			if (top < 0) {
				cout << "Stack is empty" << endl;
				return;
			}
			for (int i = top; i >= 0; i--) {
				cout << arr[i] << " ";
			}
			cout << endl;
		}
};

int main() {
	Stack s;
	s.push(10);
	s.push(20);
	s.display();
	s.pop();
	s.display();
	return 0;
}

