};

class BST {
	private:
		Node* root;
		void insert(Node*& node, int val) {
			if (!node) {
				node = new Node(val);
			} else if (val < node->data) {
				insert(node->left, val);
			} else {
				insert(node->right, val);
			}
		}
		void inorder(Node* node) {
			if (node) {
				inorder(node->left);
				cout << node->data << " ";
				inorder(node->right);
			}
		}
	public:
		BST() : root(nullptr) {}
		void insert(int val) {
			insert(root, val);
		}
		void inorder() {
			inorder(root);
		}
};

int main() {
	BST tree;
	tree.insert(5);
	tree.insert(3);
	tree.insert(7);
	tree.insert(1);
	cout << "In-order Traversal: ";
