function fibonacci(n) {
	const sequence = [0, 1];
	for (let i = 2; i < n; i++) {
		sequence.push(sequence[i - 1] + sequence[i - 2]);
	}
	return sequence;
}

console.log('Fibonacci sequence:', fibonacci(10));

function wordCount(str, word) {
	const words = str.split(' ');
	let count = 0;
	for (const w of words) {
		if (w === word) count++;
	}
	return count;
}

