function isPalindrome(str) {
	const cleanStr = str.replace(/[^a-zA-Z0-9]/g, '').toLowerCase();
	let left = 0, right = cleanStr.length - 1;
	while (left < right) {
		if (cleanStr[left] !== cleanStr[right]) return false;
		left++;
		right--;
	}
	return true;
}
