function toRoman(num) {
	const romanNumerals = [
		['I', 'IV', 'V', 'IX'],
		['X', 'XL', 'L', 'XC'],
		['C', 'CD', 'D', 'CM'],
		['M']
	];
	let result = '';
	let divisor = 1;
	let placeValue = 0;
	while (num > 0) {
		const digit = Math.floor(num % 10);
		result = romanNumerals[placeValue][digit] + result;
		num = Math.floor(num / 10);
		placeValue++;
	}
	return result;
}
console.log('Roman numeral for 1990:', toRoman(1990));

