class Stopwatch {
	constructor() {
		this.startTime = 0;
		this.endTime = 0;
		this.running = false;
	}

	start() {
		if (!this.running) {
			this.startTime = Date.now() - (this.endTime - this.startTime);
			this.running = true;
		}
	}

	stop() {
		if (this.running) {
			this.endTime = Date.now();
			this.running = false;
		}
	}

	reset() {
		this.startTime = 0;
		this.endTime = 0;
		this.running = false;
	}

	getElapsedTime() {
		if (this.running) {
			return (Date.now() - this.startTime) / 1000;
		}
		return (this.endTime - this.startTime) / 1000;
	}
}
const stopwatch = new Stopwatch();
stopwatch.start();
setTimeout(() => {
	stopwatch.stop();
	console.log('Elapsed Time:', stopwatch.getElapsedTime(), 'seconds');
}, 2000);

