const board = [['', '', ''], ['', '', ''], ['', '', '']];

function printBoard() {
	board.forEach(row => console.log(row.join(' | ')));
	console.log('\n');
}

function checkWin(player) {
	for (let i = 0; i < 3; i++) {
		if (board[i].every(cell => cell === player) || board.map(row => row[i]).every(cell => cell === player)) return true;
	}
	return (
		(board[0][0] === player && board[1][1] === player && board[2][2] === player) ||
		(board[0][2] === player && board[1][1] === player && board[2][0] === player)
	);
}

function playMove(player, x, y) {
	if (board[x][y] === '') {
		board[x][y] = player;
		printBoard();
		if (checkWin(player)) {
			console.log(`${player} wins!`);
			return;
		}
	}
}

